function confirmPNotify() {
  const notice = PNotify.notice({
    title: 'Confirmation Needed',
    text: 'Are you sure?',
    icon: 'fa fa-question-circle',
    hide: false,
    modules: {
      Confirm: {
        confirm: true
      },
      Buttons: {
        closer: false,
        sticker: false
      },
      History: {
        history: false
      }
